mapper.insert:
insert into aml_emp_post (SEQNO, VERSION, EMP_INNER_CODING, 
  POST_INNER_CODING, STATUS, MAIN_POST
  )
values (#{seqno,jdbcType=VARCHAR}, #{version,jdbcType=INTEGER}, #{empInnerCoding,jdbcType=VARCHAR}, 
  #{postInnerCoding,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{mainPost,jdbcType=VARCHAR}
  )

mapper.select:
	  select
	  
	  from aml_emp_post
	  where 1=1	
      
        and VERSION = #{version,jdbcType=INTEGER}
      
      
        and EMP_INNER_CODING = #{empInnerCoding,jdbcType=VARCHAR}
      
      
        and POST_INNER_CODING = #{postInnerCoding,jdbcType=VARCHAR}
      
      
        and STATUS = #{status,jdbcType=VARCHAR}
      
      
        and MAIN_POST = #{mainPost,jdbcType=VARCHAR}
