mapper.select:
select

from fdfb_bims_area
where 1=1


	and AREANAME = #{areaname,jdbcType=VARCHAR}


	and CLEANINGID = #{cleaningid,jdbcType=VARCHAR}


	and MAINTAINID = #{maintainid,jdbcType=VARCHAR}


	and ISPACK = #{ispack,jdbcType=VARCHAR}


	and PACKCOMPANY = #{packcompany,jdbcType=VARCHAR}


	and PACKFAULTTYPE = #{packfaulttype,jdbcType=VARCHAR}


	order by ${orderByStr}

select count(1)
from fdfb_bims_area
where 1=1

	and AREANAME = #{areaname,jdbcType=VARCHAR}


	and CLEANINGID = #{cleaningid,jdbcType=VARCHAR}


	and MAINTAINID = #{maintainid,jdbcType=VARCHAR}


	and ISPACK = #{ispack,jdbcType=VARCHAR}


	and PACKCOMPANY = #{packcompany,jdbcType=VARCHAR}


	and PACKFAULTTYPE = #{packfaulttype,jdbcType=VARCHAR}
