mapper.update:
update fdfb_department
set depname = #{depname,jdbcType=VARCHAR},
depdesc = #{depdesc,jdbcType=VARCHAR},
createon = #{createon,jdbcType=TIMESTAMP},
createby = #{createby,jdbcType=VARCHAR},
modifyon = #{newModifyon,jdbcType=TIMESTAMP},
modifyby = #{modifyby,jdbcType=VARCHAR}
where seqno = #{seqno,jdbcType=VARCHAR}
and modifyon = #{modifyon,jdbcType=TIMESTAMP}

mapper.select:
select

from fdfb_department
where 1=1

	and depname = #{depname,jdbcType=VARCHAR}


	and depdesc = #{depdesc,jdbcType=VARCHAR}



	order by ${orderByStr}

select count(1)
from fdfb_department
where 1=1

	and depname = #{depname,jdbcType=VARCHAR}


	and depdesc = #{depdesc,jdbcType=VARCHAR}
