mapper.select:
select

from fdfb_permit a
where parentseqno = #{parentseqno,jdbcType=VARCHAR}
and permittype = #{permittype,jdbcType=VARCHAR} 

	and EXISTS(select 0 from fdfb_rolepermit b where a.seqno = b.permitid and b.roleid in (${roleId}) )

order by ${orderByStr}

select

from fdfb_permit a
where permittype = #{permittype,jdbcType=VARCHAR} 
and parentseqno is NULL

	and EXISTS(select 0 from fdfb_rolepermit b where a.seqno = b.permitid and b.roleid in (${roleId}) )

order by ${orderByStr}

select

from fdfb_permit a
where permittype = #{permittype,jdbcType=VARCHAR} 
and isleaf = 'Y'

	and EXISTS(select 0 from fdfb_rolepermit b where a.seqno = b.permitid and b.roleid in (${roleId}) )

order by ${orderByStr}
