mapper.select:
SELECT seqno,zipname,filename, CASE when sfid='CTFL' then'文件内容错误' when sfid='RQCR' then '文件内容待补正' else'文件发送正确' end sfid, ertn FROM t_receipt_manage WHERE seqno IN ( SELECT max(seqno) FROM t_receipt_manage t GROUP BY t.filename )
and zipname = #{zipName,jdbcType=VARCHAR}
and filename = #{fileName,jdbcType=VARCHAR}
and sfid = #{sfid,jdbcType=VARCHAR}

select count(1)
from t_receipt_manage
where seqno IN (
	SELECT
		max(seqno)
	FROM
		t_receipt_manage t
	GROUP BY
		t.filename
)

	and zipname = #{zipName,jdbcType=VARCHAR}


	and filename = #{fileName,jdbcType=VARCHAR}


	and sfid = #{sfid,jdbcType=VARCHAR}
