mapper.select:
select fu.username,fu.seqno,fu.name, fr.rolename from  fdfb_user fu LEFT JOIN fdfb_user_role fur on fu.seqno=fur.userid 
	LEFT JOIN fdfb_role fr on fr.seqno=fur.roleid 
	where 1=1

	and fu.username =#{username,jdbcType=VARCHAR}


	and fr.seqno =#{seqno,jdbcType=VARCHAR}

mapper.insert:
insert into fdfb_role (seqno, rolename,
roledesc, createon,
createby, modifyon, modifyby
)
values (#{seqno,jdbcType=VARCHAR},
#{rolename,jdbcType=VARCHAR},
#{roledesc,jdbcType=VARCHAR}, #{createon,jdbcType=TIMESTAMP},
#{createby,jdbcType=VARCHAR}, #{modifyon,jdbcType=TIMESTAMP},
#{modifyby,jdbcType=VARCHAR}
)

mapper.update:
update fdfb_role
set rolename = #{rolename,jdbcType=VARCHAR},
roledesc = #{roledesc,jdbcType=VARCHAR},
createon = #{createon,jdbcType=TIMESTAMP},
createby = #{createby,jdbcType=VARCHAR},
modifyon = #{newMofifyon,jdbcType=TIMESTAMP},
modifyby = #{modifyby,jdbcType=VARCHAR}
where seqno = #{seqno,jdbcType=VARCHAR}
and modifyon = #{modifyon,jdbcType=TIMESTAMP}

mapper.select:
select

from fdfb_role
where 1=1

	and rolename = #{rolename,jdbcType=VARCHAR}


	and roledesc = #{roledesc,jdbcType=VARCHAR}



	order by ${orderByStr}

select count(1)
from fdfb_role
where 1=1

	and rolename = #{rolename,jdbcType=VARCHAR}


	and roledesc = #{roledesc,jdbcType=VARCHAR}
