mapper.select:
select

from fdfb_template
where 1=1

	and templateName = #{templateName,jdbcType=VARCHAR}


	and templateType = #{templateType,jdbcType=VARCHAR}


	and receiver = #{receiver,jdbcType=VARCHAR}


	order by ${orderByStr}

select count(1)
from fdfb_template
where 1=1

	and templateName = #{templateName,jdbcType=VARCHAR}


	and templateType = #{templateType,jdbcType=VARCHAR}


	and receiver = #{receiver,jdbcType=VARCHAR}
