select user.*,org_name,new_org_no
from fdfb_user user,aml_emp_post e,aml_post_unit p,aml_unit unit,aml_org o
where user.emp_inner_coding = e.emp_inner_coding 
and e.post_inner_coding = p.post_inner_coding
and p.unit_inner_coding = unit.unit_inner_coding
and unit.org_id = o.org_inner_coding
and e.status = '1'
and e.main_post = '1'
and p.status = '1'
and unit.status = '1'
and o.status = '1'

	and id = #{id,jdbcType=VARCHAR}


	and name = #{name,jdbcType=VARCHAR}


	and username = #{username,jdbcType=VARCHAR}


	and xb = #{xb,jdbcType=VARCHAR}


	and email = #{email,jdbcType=VARCHAR}


	and departmentid = #{departmentid,jdbcType=VARCHAR}


	and groupid = #{groupid,jdbcType=VARCHAR}


	and emp_inner_coding = #{empInnerCoding,jdbcType=VARCHAR}


	and emp_no = #{empNo,jdbcType=VARCHAR}


	and new_org_no = #{newOrgNo,jdbcType=VARCHAR}

select count(*)
from fdfb_user user,aml_emp_post e,aml_post_unit p,aml_unit unit,aml_org o
