mapper.select:
		select

		from t_honor
		where 1=1

            and honor_organ like '%${honorOrgan}%'


      		and insert_time >= #{startInsertTime,jdbcType=TIMESTAMP}


      		and insert_time <= #{endInsertTime,jdbcType=TIMESTAMP}


      		and honor_time >= #{startHonorTime,jdbcType=TIMESTAMP}


      		and honor_time <= #{endHonorTime,jdbcType=TIMESTAMP}


            and honor_dept like '%${honorDept}%'

		select count(1)
		from t_honor
		where 1=1

            and honor_organ like '%${honorOrgan}%'


      		and insert_time >= #{startInsertTime,jdbcType=TIMESTAMP}


      		and insert_time <= #{endInsertTime,jdbcType=TIMESTAMP}


      		and honor_time >= #{startHonorTime,jdbcType=TIMESTAMP}


      		and honor_time <= #{endHonorTime,jdbcType=TIMESTAMP}


            and honor_dept = #{honorDept,jdbcType=VARCHAR}
