mapper.select:
		select

		from t_inspect
		where 1=1

        and organ_id like '%${organId}%'


      		and insert_time >= #{startInsertTime,jdbcType=TIMESTAMP}


      		and insert_time <= #{endInsertTime,jdbcType=TIMESTAMP}


      		and inspect_time >= #{startInspectTime,jdbcType=TIMESTAMP}


      		and inspect_time <= #{endInspectTime,jdbcType=TIMESTAMP}


            and inspect_organ like '%${inspectOrgan}%'

		select count(1)
		from t_inspect
		where 1=1

        and organ_id like '%${organId}%'


      		and insert_time >= #{startInsertTime,jdbcType=TIMESTAMP}


      		and insert_time <= #{endInsertTime,jdbcType=TIMESTAMP}


      		and inspect_time >= #{startInspectTime,jdbcType=TIMESTAMP}


      		and inspect_time <= #{endInspectTime,jdbcType=TIMESTAMP}


            and inspect_organ like '%${inspectOrgan}%'
