mapper.select:
  	select
	
	from aml_customer_legal_info
	where 1=1
	
       AND unit = #{unit,jdbcType=VARCHAR}
    
    
       AND gmt_last_issue_time >= #{startGmtLastIssueTime,jdbcType=TIMESTAMP}
    
    
       AND gmt_last_issue_time < date_add(#{endGmtLastIssueTime,jdbcType=TIMESTAMP}, interval 1 day)

  	select
	
	from aml_customer_legal_info
	where 1=1
	
       AND unit = #{unit,jdbcType=VARCHAR}
    
    
       AND gmt_last_issue_time >= #{startGmtLastIssueTime,jdbcType=TIMESTAMP}
    
    
       AND gmt_last_issue_time < date_add(#{endGmtLastIssueTime,jdbcType=TIMESTAMP}, interval 1 day)

  	select count(1)
	from aml_customer_legal_info
	where 1=1
	
       AND unit = #{unit,jdbcType=VARCHAR}
    
    
       AND gmt_last_issue_time >= #{startGmtLastIssueTime,jdbcType=TIMESTAMP}
    
    
       AND gmt_last_issue_time < date_add(#{endGmtLastIssueTime,jdbcType=TIMESTAMP}, interval 1 day)
