mapper.select:
	  select

	  from aml_org
	  where 1=1

        and org_inner_coding = #{orgInnerCoding,jdbcType=VARCHAR}


        and org_no = #{orgNo,jdbcType=VARCHAR}


        and org_name = #{orgName,jdbcType=VARCHAR}


        and parent_compnay_id = #{parentCompnayId,jdbcType=VARCHAR}


       and p_org_inncoding = #{pOrgInncoding,jdbcType=VARCHAR}


       and status = #{status,jdbcType=VARCHAR}


       and new_org_no = #{newOrgNo,jdbcType=VARCHAR}
