mapper.update:
update aml_post_unit
set VERSION = #{version,jdbcType=INTEGER},
  POST_INNER_CODING = #{postInnerCoding,jdbcType=VARCHAR},
  UNIT_INNER_CODING = #{unitInnerCoding,jdbcType=VARCHAR},
  STATUS = #{status,jdbcType=VARCHAR}
where SEQNO = #{seqno,jdbcType=VARCHAR}

mapper.select:
	  select

	  from aml_post_unit
	  where 1=1

        and VERSION = #{version,jdbcType=INTEGER}


        and POST_INNER_CODING = #{postInnerCoding,jdbcType=VARCHAR}


        and UNIT_INNER_CODING = #{unitInnerCoding,jdbcType=VARCHAR}


        and STATUS = #{status,jdbcType=VARCHAR}
