mapper.select:
		select

		from t_punish
		where 1=1

	        and punish_organ like '%${punishOrgan}%'


      		and insert_time >= #{startInsertTime,jdbcType=TIMESTAMP}


      		and insert_time <= #{endInsertTime,jdbcType=TIMESTAMP}


      		and punish_time >= #{startPunishTime,jdbcType=TIMESTAMP}


      		and punish_time <= #{endPunishTime,jdbcType=TIMESTAMP}


            and organ_id like '%${organId}%'

		select count(1)
		from t_punish
		where 1=1

            and punish_organ like '%${punishOrgan}%'


      		and insert_time >= #{startInsertTime,jdbcType=TIMESTAMP}


      		and insert_time <= #{endInsertTime,jdbcType=TIMESTAMP}


      		and punish_time >= #{startPunishTime,jdbcType=TIMESTAMP}


      		and punish_time <= #{endPunishTime,jdbcType=TIMESTAMP}


            and organ_id like '%${organId}%'

mapper.insert:
insert into t_punish (seqno, insert_time, punish_time, 
  punish_organ, organ_id, punish_reason, 
  punish_content, punish_way, punish_measure, 
  upload, remark, path
  )
values (#{seqno,jdbcType=VARCHAR}, #{insertTime,jdbcType=TIMESTAMP}, #{punishTime,jdbcType=TIMESTAMP}, 
  #{punishOrgan,jdbcType=VARCHAR}, #{organId,jdbcType=VARCHAR}, #{punishReason,jdbcType=VARCHAR}, 
  #{punishContent,jdbcType=VARCHAR}, #{punishWay,jdbcType=VARCHAR}, #{punishMeasure,jdbcType=VARCHAR}, 
  #{upload,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{path,jdbcType=VARCHAR}
  )
