mapper.select:
		select
		
		from t_baseprocedure
		where 1=1
      
        and CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
      
      
        and NUM = #{num,jdbcType=VARCHAR}
      
      
        and RISK_SUBITEM = #{riskSubitem,jdbcType=VARCHAR}
      
      
        and ONE = #{one,jdbcType=VARCHAR}
      
      
        and TWO = #{two,jdbcType=VARCHAR}
      
      
        and THREE = #{three,jdbcType=VARCHAR}
      
      
        and FOUR = #{four,jdbcType=VARCHAR}
      
      
        and SCORE = #{score,jdbcType=VARCHAR}
      
      
        and FROM_WEIGHT = #{fromWeight,jdbcType=VARCHAR}
      
      
        and PRONO = #{prono,jdbcType=VARCHAR}
      
      
        and CREATEBY = #{createby,jdbcType=VARCHAR}
      
      
        and CREATEON = #{createon,jdbcType=TIMESTAMP}
      
      
        and MODIFYBY = #{modifyby,jdbcType=VARCHAR}
      
      
        and MODIFYON = #{modifyon,jdbcType=TIMESTAMP}
      
	  order by CAST(num AS decimal(3,2)) asc

		select count(1)
		from t_baseprocedure
		where 1=1
      
        and CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
      
      
        and NUM = #{num,jdbcType=VARCHAR}
      
      
        and RISK_SUBITEM = #{riskSubitem,jdbcType=VARCHAR}
      
      
        and ONE = #{one,jdbcType=VARCHAR}
      
      
        and TWO = #{two,jdbcType=VARCHAR}
      
      
        and THREE = #{three,jdbcType=VARCHAR}
      
      
        and FOUR = #{four,jdbcType=VARCHAR}
      
      
        and SCORE = #{score,jdbcType=VARCHAR}
      
      
        and FROM_WEIGHT = #{fromWeight,jdbcType=VARCHAR}
      
      
        and PRONO = #{prono,jdbcType=VARCHAR}
      
      
        and CREATEBY = #{createby,jdbcType=VARCHAR}
      
      
        and CREATEON = #{createon,jdbcType=TIMESTAMP}
      
      
        and MODIFYBY = #{modifyby,jdbcType=VARCHAR}
      
      
        and MODIFYON = #{modifyon,jdbcType=TIMESTAMP}
