mapper.insert:
insert into t_riskscore_log (SEQNO, CUSTOMER_ID, CUSTOMER_NAME, 
  OLD_LEVEL, NEW_LEVEL, SUGGESTION, 
  OPERATION, OPERATION_TIME, OPERATION_PERSON
  )
values (#{seqno,jdbcType=VARCHAR}, #{customerId,jdbcType=VARCHAR}, #{customerName,jdbcType=VARCHAR}, 
  #{oldLevel,jdbcType=VARCHAR}, #{newLevel,jdbcType=VARCHAR}, #{suggestion,jdbcType=VARCHAR}, 
  #{operation,jdbcType=VARCHAR}, #{operationTime,jdbcType=TIMESTAMP}, #{operationPerson,jdbcType=VARCHAR}
  )

mapper.select:
		select

		from t_riskscore_log
		where 1=1

        and CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}


        and CUSTOMER_NAME = #{customerName,jdbcType=VARCHAR}


        and OLD_LEVEL = #{oldLevel,jdbcType=VARCHAR}


        and NEW_LEVEL = #{newLevel,jdbcType=VARCHAR}


        and SUGGESTION = #{suggestion,jdbcType=VARCHAR}


        and OPERATION = #{operation,jdbcType=VARCHAR}


        and OPERATION_TIME = #{operationTime,jdbcType=TIMESTAMP}


        and OPERATION_PERSON = #{operationPerson,jdbcType=VARCHAR}

      order by OPERATION_TIME desc

		select count(1)
		from t_riskscore_log
		where 1=1

        and CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}


        and CUSTOMER_NAME = #{customerName,jdbcType=VARCHAR}


        and OLD_LEVEL = #{oldLevel,jdbcType=VARCHAR}


        and NEW_LEVEL = #{newLevel,jdbcType=VARCHAR}


        and SUGGESTION = #{suggestion,jdbcType=VARCHAR}


        and OPERATION = #{operation,jdbcType=VARCHAR}


        and OPERATION_TIME = #{operationTime,jdbcType=TIMESTAMP}


        and OPERATION_PERSON = #{operationPerson,jdbcType=VARCHAR}

      order by OPERATION_TIME desc
