mapper.select:
		select
		
		from t_sco
		where 1=1
		
        	and	CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
      	
      	
        	and	CUSTOMER_NAME = #{customerName,jdbcType=VARCHAR}
      	
      	
        	and	STATUS != #{status,jdbcType=VARCHAR}

		select
		
		from t_sco
		where 1=1
		
      		and CUSTOMER_ID like '${customerId}%'
      	
      	
        	and CUSTOMER_NAME like '%${customerName}%'
      	
      	
        	and IDENTIFY_TYPE = #{identifyType,jdbcType=VARCHAR}
      	
      	
        	and IDENTIFY_NUMBER = #{identifyNumber,jdbcType=VARCHAR}
      	
      	
        	and ORG_CODE like '${orgCode}%'
      	
      	
        	and PREVIOUS_GRADETYPE = #{previousGradetype,jdbcType=VARCHAR}
      	
      	
        	and GRADE_TYPE = #{gradeType,jdbcType=VARCHAR}
      	
      	
      		
        	and PREVIOUS_SCORE <= 29
        	
        	
        	and PREVIOUS_SCORE >= 30
        	and PREVIOUS_SCORE <= 49
        	
        	
        	and PREVIOUS_SCORE >= 50
        	and PREVIOUS_SCORE <= 69
        	
        	
        	and PREVIOUS_SCORE >= 70
        	
      	
      	
      		and SCORE_TIME >= #{startScoreTime,jdbcType=TIMESTAMP}
      	
      	
      		and SCORE_TIME <= date_add(#{endScoreTime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
        	and PREVIOUS_GRADE = #{previousGrade,jdbcType=VARCHAR}
      	
      	
        	and PRESENT_GRADE = #{presentGrade,jdbcType=VARCHAR}
      	
      	
      		and GRADE_TIME >= #{startGradeTime,jdbcType=TIMESTAMP}
      	
      	
        	and GRADE_TIME <= date_add(#{endGradeTime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
        	and SUGGESTION = #{suggestion,jdbcType=VARCHAR}
      	
        
        
      		
      		
      			
      				and (status = 0 or status = 6)
      			
      			
      				and (status = 4 or status = 8)
      			
      			
        		and status = #{status,jdbcType=VARCHAR}
        		
      		
      		
        	
        	and status = #{status,jdbcType=VARCHAR}
        	
       	
      	
        
        	and RETURN_SUGGESTION = #{returnSuggestion,jdbcType=VARCHAR}
       	
      	
        	and CANCLE_SUGGESTION = #{cancleSuggestion,jdbcType=VARCHAR}
      	
      	
        	and NEXT_REVIEWTIME >= #{startNextReviewtime,jdbcType=TIMESTAMP}
       	
      	
        	and NEXT_REVIEWTIME <= date_add(#{endNextReviewtime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
      		and PREVIOUS_GRADETIME >= #{startPreviousGradetime,jdbcType=TIMESTAMP}
      	
      	
        	and PREVIOUS_GRADETIME <= date_add(#{endPreviousGradetime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
        	and MOD_GRADE = #{modGrade,jdbcType=VARCHAR}
      	
      	
        	and FIRST_TIME >= #{startFirstTime,jdbcType=TIMESTAMP}
      	
      	
        	and FIRST_TIME <= date_add(#{endFirstTime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
      	
        	and REVIEW like '%${review}%'
      	
      	
        	and FROMUSER = #{fromuser,jdbcType=VARCHAR}
      	
      	 
        	and #{customerType,jdbcType=VARCHAR}
      	
      	
        	and #{isAntCustomer,jdbcType=VARCHAR}
        
		
			order by ${orderByStr}

		select count(1)
		from t_sco
		where 1=1
		
      		and CUSTOMER_ID like '${customerId}%'
      	
      	
        	and CUSTOMER_NAME like '%${customerName}%'
      	
      	
        	and IDENTIFY_TYPE = #{identifyType,jdbcType=VARCHAR}
      	
      	
        	and IDENTIFY_NUMBER = #{identifyNumber,jdbcType=VARCHAR}
      	
      	
        	and ORG_CODE like '${orgCode}%'
      	
      	
        	and PREVIOUS_GRADETYPE = #{previousGradetype,jdbcType=VARCHAR}
      	
      	
        	and GRADE_TYPE = #{gradeType,jdbcType=VARCHAR}
      	
      	
      		
        	and PREVIOUS_SCORE <= 29
        	
        	
        	and PREVIOUS_SCORE >= 30
        	and PREVIOUS_SCORE <= 49
        	
        	
        	and PREVIOUS_SCORE >= 50
        	and PREVIOUS_SCORE <= 69
        	
        	
        	and PREVIOUS_SCORE >= 70
        	
      	
      	
      		and SCORE_TIME >= #{startScoreTime,jdbcType=TIMESTAMP}
      	
      	
     		and SCORE_TIME <= date_add(#{endScoreTime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
        	and PREVIOUS_GRADE = #{previousGrade,jdbcType=VARCHAR}
      	
      	
        	and PRESENT_GRADE = #{presentGrade,jdbcType=VARCHAR}
      	
      	
        	and GRADE_TIME = #{gradeTime,jdbcType=TIMESTAMP}
      	
      	
        	and SUGGESTION = #{suggestion,jdbcType=VARCHAR}
      	
        
        
      		
      		
      			
      				and (status = 0 or status = 6)
      			
      			
      				and (status = 2 or status = 7)
      			
      			
      				and (status = 3 or status = 8)
      			
      			
        		and status = #{status,jdbcType=VARCHAR}
        		
      		
      		
        	
        	and status = #{status,jdbcType=VARCHAR}
        	
       	
      	
        
        	and RETURN_SUGGESTION = #{returnSuggestion,jdbcType=VARCHAR}
       	
      	
        	and CANCLE_SUGGESTION = #{cancleSuggestion,jdbcType=VARCHAR}
      	
      	
        	and NEXT_REVIEWTIME = #{nextReviewtime,jdbcType=TIMESTAMP}
      	
      	
      		and PREVIOUS_GRADETIME >= #{startPreviousGradetime,jdbcType=TIMESTAMP}
      	
      	
        	and PREVIOUS_GRADETIME <= date_add(#{endPreviousGradetime,jdbcType=TIMESTAMP}, interval 1 day)
      	
      	
        	and MOD_GRADE = #{modGrade,jdbcType=VARCHAR}
      	
      	
        	and FIRST_TIME = #{firstTime,jdbcType=TIMESTAMP}
      	
      	
        	and CUSTOMER_NAME like '%${customerName}%'
      	
      	
        	and FROMUSER = #{fromuser,jdbcType=VARCHAR}
      	
      	 
        	and #{customerType,jdbcType=VARCHAR}
      	
      	
        	and #{isAntCustomer,jdbcType=VARCHAR}
        
		
			order by ${orderByStr}
