SELECT sd.seqno, sd.SSDS, sd.STCR, sd.SDGR, sd.CONDITION_CODE, sd.INSERT_ORGAN_ID, sd.FIRST_ID, sd.STATUS, sd.EXPLICATION, sd.INSERT_TIME, sd.TKMS, scm.CUSTOMER_NAME, sct.POLICY_NO FROM sus_data sd LEFT JOIN sus_customer scm ON sd.seqno = scm.su_data_id LEFT JOIN sus_contract sct ON sd.seqno = sct.su_data_id WHERE ( sd.`STATUS` = '10' OR sd.`STATUS` = '21')
and INSERT_ORGAN_ID = #{insertOrganId,jdbcType=VARCHAR}
and CONDITION_CODE = #{conditionCode,jdbcType=VARCHAR}
and STATUS = #{status,jdbcType=VARCHAR}
and FIRST_ID = #{firstId,jdbcType=VARCHAR}
and scm.CUSTOMER_NAME = #{customerName,jdbcType=VARCHAR}
and POLICY_NO = #{policyNo,jdbcType=VARCHAR}

     	SELECT 
			count(1)		
		FROM
			sus_data sd 
			left join sus_customer scm ON sd.seqno = scm.su_data_id
			left join sus_contract sct on sd.seqno = sct.su_data_id
		WHERE
			( sd.`STATUS` = '10' OR sd.`STATUS` = '21') 
	      
	       and INSERT_ORGAN_ID = #{insertOrganId,jdbcType=VARCHAR}
	      
	      
		  
     		  and CONDITION_CODE = #{conditionCode,jdbcType=VARCHAR}
     	  

	      
	       and STATUS = #{status,jdbcType=VARCHAR}
	      
	    
	      
	       and FIRST_ID = #{firstId,jdbcType=VARCHAR}
	      
	      
		  
     		  and scm.CUSTOMER_NAME = #{customerName,jdbcType=VARCHAR}
     	  	      
     	  
		  
     		  and sct.POLICY_NO = #{policyNo,jdbcType=VARCHAR}

