mapper.select:
select 

from sus_report_log
 where 1=1
  
   AND REPORT_TYPE = #{reportType,jdbcType=VARCHAR}
  
  
   AND TRADE_FEATURE = #{tradeFeature,jdbcType=VARCHAR}
  
  
   AND FILE_TYPE = #{fileType,jdbcType=VARCHAR}
  
  
   AND FILE_NAME = #{fileName,jdbcType=VARCHAR}
  
  
   AND GENERATE_TIME = #{generateTime,jdbcType=VARCHAR}
  
  
   AND REPORT_TIME = #{reportTime,jdbcType=VARCHAR}
     
  
   AND REPORT_TIME is null
        
  
   AND ZIP_NAME = #{zipName,jdbcType=VARCHAR}
  
  
   AND ZIP_NAME is null

select 
count(1)
from sus_report_log
where 1=1
  
   AND REPORT_TYPE = #{reportType,jdbcType=VARCHAR}
  
  
   AND TRADE_FEATURE = #{tradeFeature,jdbcType=VARCHAR}
  
  
   AND FILE_TYPE = #{fileType,jdbcType=VARCHAR}
  
  
   AND FILE_NAME = #{fileName,jdbcType=VARCHAR}
  
  
   AND GENERATE_TIME = #{generateTime,jdbcType=VARCHAR}
  
  
   AND REPORT_TIME = #{reportTime,jdbcType=VARCHAR}
     
  
   AND REPORT_TIME is null
        
  
   AND ZIP_NAME = #{zipName,jdbcType=VARCHAR}
