mapper.select:
SELECT 

FROM sus_transinfo st
WHERE 1=1 
  
   AND st.SU_DATA_ID = #{suDataId,jdbcType=VARCHAR}
  
  
   AND st.TRADE_FEATURE = #{tradeFeature,jdbcType=VARCHAR}
  
  
   AND st.ISTP = #{istp,jdbcType=VARCHAR}
  
  
   AND st.PROD_NAME = #{prodName,jdbcType=VARCHAR}
  
  
   AND st.ISOG = #{isog,jdbcType=VARCHAR}
  
  
   AND st.PARTY_ID = #{partyId,jdbcType=VARCHAR}
  
  
   AND st.ORGAN_CODE = #{organCode,jdbcType=VARCHAR}
  
  
   AND st.CUSTOMER_NAME = #{customerName,jdbcType=VARCHAR}
  
  
   AND st.CUSTOMER_CERTI_TYPE = #{customerCertiType,jdbcType=VARCHAR}
  
  
   AND st.CUSTOMER_OTHER_CERTI_TYPE = #{customerOtherCertiType,jdbcType=VARCHAR}
  
  
   AND st.CUSTOMER_CERTI_CODE = #{customerCertiCode,jdbcType=VARCHAR}
  
  
   AND st.POLICY_NO = #{policyNo,jdbcType=VARCHAR}
  
  
   AND st.POLICY_EFFECT_TIME = #{policyEffectTime,jdbcType=VARCHAR}
  
  
   AND st.ISAT = #{isat,jdbcType=VARCHAR}
  
  
   AND st.ISFE = #{isfe,jdbcType=VARCHAR}
  
  
   AND st.ISPT = #{ispt,jdbcType=VARCHAR}
  
  
   AND st.CTES = #{ctes,jdbcType=VARCHAR}
  
  
   AND st.INSURED_NAME = #{insuredName,jdbcType=VARCHAR}
  
  
   AND st.INSURED_CERTI_TYPE = #{insuredCertiType,jdbcType=VARCHAR}
  
  
   AND st.INSURED_CERTI_OTHER_TYPE = #{insuredCertiOtherType,jdbcType=VARCHAR}
  
  
   AND st.INSURED_CERTI_CODE = #{insuredCertiCode,jdbcType=VARCHAR}
  
  
   AND st.RELATIONSHIP = #{relationship,jdbcType=VARCHAR}
  
  
   AND st.BENIFIT_NAME = #{benifitName,jdbcType=VARCHAR}
  
  
   AND st.BENIFIT_CERTI_CODE = #{benifitCertiCode,jdbcType=VARCHAR}
  
  
   AND st.BENIFIT_OTHER_CERTI_TYPE = #{benifitOtherCertiType,jdbcType=VARCHAR}
  
  
   AND st.BENIFIT_CERTI_TYPE = #{benifitCertiType,jdbcType=VARCHAR}
  
  
   AND st.ORGAN_ID = #{organId,jdbcType=VARCHAR}
  
  
   AND st.CRDR = #{crdr,jdbcType=VARCHAR}
  
  
   AND st.CSTP = #{cstp,jdbcType=VARCHAR}
  
  
   AND st.REMARK_ONE = #{remarkOne,jdbcType=VARCHAR}
  
  
   AND st.REMARK_TWO = #{remarkTwo,jdbcType=VARCHAR}
  
  
   AND st.ADDTIME = #{addtime,jdbcType=VARCHAR}
  
  
   AND st.CUR_KIND = #{curKind,jdbcType=VARCHAR}
  
  
   AND st.TRADE_TIME = #{tradeTime,jdbcType=VARCHAR}
  
  
   AND st.CUR_PREM = #{curPrem,jdbcType=VARCHAR}
  
  
   AND st.PROD_TRADE_TYPE = #{prodTradeType,jdbcType=VARCHAR}
  
  
   AND st.UNIT = #{unit,jdbcType=VARCHAR}

SELECT 

FROM sus_transinfo st
WHERE st.SU_DATA_ID = #{suDataId,jdbcType=VARCHAR}
