mapper.select:
		select

		from t_system_file
		where 1=1 and organ_Id=#{organId,jdbcType=VARCHAR} 

      and organ_name like '%${organName}%'


      and publish_time >= #{beginDate,jdbcType=VARCHAR}


      and #{endDate,jdbcType=VARCHAR} >= publish_time

		select count(1)
		from t_system_file
		where 1=1 and organ_Id=#{organId,jdbcType=VARCHAR} 

      and organ_name like '%${organName}%'


      and publish_time >= #{beginDate,jdbcType=VARCHAR}


      and #{endDate,jdbcType=VARCHAR} >= publish_time
