mapper.select:
		select

		from t_train
		where 1=1 and organ_id=#{organId,jdbcType=VARCHAR}

       and organ_name like '%${organName}%'


       and active_time = #{activeTime,jdbcType=Date}


      and active_time >= #{beginDate,jdbcType=VARCHAR}


      and #{endDate,jdbcType=VARCHAR} >= active_time

		select count(1)
		from t_train
		where 1=1 and organ_id=#{organId,jdbcType=VARCHAR}

       and organ_name like '%${organ_name}%'


       and active_time = #{activeTime,jdbcType=TIMESTAMP}


      and active_time >= #{beginDate,jdbcType=VARCHAR}


      and #{endDate,jdbcType=VARCHAR} >= active_time
