mapper.update:
update aml_unit
set VERSION = #{version,jdbcType=INTEGER},
  UNIT_INNER_CODING = #{unitInnerCoding,jdbcType=VARCHAR},
  ORG_ID = #{orgId,jdbcType=VARCHAR},
  STATUS = #{status,jdbcType=VARCHAR}
where SEQNO = #{seqno,jdbcType=VARCHAR}

mapper.select:
	  select

